mapper.select:
SELECT *
FROM followings
WHERE source_user_id = #{source_user_id} AND target_user_id = #{target_user_id}

SELECT *
FROM blockings
WHERE source_user_id = #{source_user_id} AND target_user_id = #{target_user_id}

mapper.update:
UPDATE users
SET user_login_time = NOW(), user_accesstoken = #{user_accesstoken}, user_refreshtoken = #{user_refreshtoken}
WHERE user_id = #{user_id} AND user_status = 'N'

UPDATE users
SET user_accesstoken = #{user_accesstoken}, user_refreshtoken = #{user_refreshtoken}
WHERE user_id = #{user_id} AND user_status = 'N'
